import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import '../style/CollegeViewbook.css';
import BasicInfoRow from './BasicInfoRow';
import MajorRow from './MajorRow.js';
import AppRow from './AppRow.js';
import TuitionRow from './TuitionRow.js';

export default class CollegeViewbook extends React.Component {
	constructor(props) {
        super(props);

		/* Make any necessary changes to state */
		this.state = {
			majors: [],
			infos: [],
			tuitions: [],
			applications: [],
			text: ""
		}

		this.getApplicationInfo = this.getApplicationInfo.bind(this);
		this.getMajorInfo = this.getMajorInfo.bind(this);
		this.getNextSteps = this.getNextSteps.bind(this);
		this.getTuitionInfo = this.getTuitionInfo.bind(this);
	}

	/* ---- Add methods and modify render function below ---- */
	componentDidMount() {
		fetch("http://localhost:8081/viewbook-majors", {
			method: 'GET',
		}).then(res => {
			return res.json();
		}, err => {
			console.log(err);
		}
		).then(majorsList => {
			if(!majorsList) return;
			let majorsDivs = majorsList.map((majorObj, i) =>
				<MajorRow title={majorObj.title}/>
			);

			//set state
			this.setState({
				majors: majorsDivs
			})
		}, err => {
			console.log(err);
		});

		fetch("http://localhost:8081/viewbook-basic-info", {
			method: 'GET',
		}).then(res => {
			return res.json();
		}, err => {
			console.log(err);
		}
		).then(infoList => {
			if(!infoList) return;
			let infoDivs = infoList.map((infoObj, i) =>
				<BasicInfoRow name={infoObj.name} address={infoObj.address} state={infoObj.state} city={infoObj.city} zip={infoObj.zip} phone={infoObj.phone} degree_urbanization={infoObj.degree_urbanization}/>
			);

			//set state
			this.setState({
				infos: infoDivs
			})
		}, err => {
			console.log(err);
		});
	}

	/* onClick methods here*/

	/* getMajorInfo */
	getMajorInfo(){
		fetch("http://localhost:8081/viewbook-majors", {
			method: 'GET',
		}).then(res => {
			return res.json();
		}, err => {
			console.log(err);
		}
		).then(majorsList => {
			if(!majorsList) return;
			let majorsDivs = majorsList.map((majorObj, i) =>
				<MajorRow title={majorObj.title}/>
			);

			//set state
			this.setState({
				majors: majorsDivs,
				tuitions: [],
				applications: [],
				text: ""
			})
		}, err => {
			console.log(err);
		});
	}

	/* getApplicationInfo */
	getApplicationInfo(){
		fetch("http://localhost:8081/viewbook-application", {
			method: 'GET',
		}).then(res => {
			return res.json();
		}, err => {
			console.log(err);
		}
		).then(appList => {
			if(!appList) return;
			let appDivs = appList.map((appObj, i) => /* fix these below */
				<AppRow percent_accepted={appObj.percent_accepted} size={appObj.size} url_school={appObj.url_school} url_application={appObj.url_application}/>
			);

			//set state
			this.setState({
				majors: [],
				tuitions: [],
				applications: appDivs,
				text: ""
			})
		}, err => {
			console.log(err);
		});
	}

	/* getTuitionInfo */
	getTuitionInfo(){
		fetch("http://localhost:8081/viewbook-tuition", {
			method: 'GET',
		}).then(res => {
			return res.json();
		}, err => {
			console.log(err);
		}
		).then(tuitionList => {
			if(!tuitionList) return;
			let tuitionDivs = tuitionList.map((tuitionObj, i) => /* fix these below */
				<TuitionRow tuition_type={tuitionObj.tuition_type} tuition_fees_ft={tuitionObj.tuition_fees_ft} url_fin_aid={tuitionObj.url_fin_aid} />
			);

			//set state
			this.setState({
				majors: [],
				tuitions: tuitionDivs,
				applications: [],
				text: ""
			})
		}, err => {
			console.log(err);
		});
	}

	/* getNextSteps*/
	getNextSteps(){
		this.setState({
			majors: [],
			tuitions: [],
			applications: [],
			text: "Basic text here"
		})
	}

	/* For sake of testing, html code from movie method here */

	render() {
		return (
			<div className="CollegeViewbook">

				<br></br>
				<div className="container viewbook-container"> 
					<div className="jumbotron">
						<div className="title"><h3>Alabama A and M Example</h3></div>
					</div>
				</div> 

				<br></br>
				<div className="container">
					<div className="col-sm main-area">
						<h4>About this School</h4>
						<br></br>
						<div className="container" id="basic-results">
							{this.state.infos}
						</div>

					</div>
					<div className="col-sm"> 
						<div id="carouselExampleSlidesOnly" class="carousel slide" data-ride="carousel">
							<div class="carousel-inner">
								<div class="carousel-item active">
									<img class="d-block w-100" src="campus_photos/picture2.jpg"/>
								</div>
								<div class="carousel-item">
									<img class="d-block w-100" src="https://www.w3schools.com/bootstrap/ny.jpg" alt="Second slide"/>
								</div>
								<div class="carousel-item">
									<img class="d-block w-100" src="https://www.w3schools.com/bootstrap/la.jpg" alt="Third slide"/>
								</div>
								<div class="carousel-item">
									<img class="d-block w-100" src="/campus_photos/picture4.jpg" alt="Third slide"/>
								</div>
								<div class="carousel-item">
									<img class="d-block w-100" src="/campus_photos/picture5.jpg" alt="Third slide"/>
								</div>
							</div>
						</div>
					</div>

				</div>

				<br></br>
				<div className="container">
					<div className="main-area col-med">
						<h4><button class="button" onClick={this.getMajorInfo}>Available Majors</button></h4>
						<h4><button class="button" onClick={this.getApplicationInfo}>Application Information</button></h4>
						<h4><button class="button" onClick={this.getTuitionInfo}>Tuition Information</button></h4>
						<h4><button class="button" onClick={this.getNextSteps}>Next Steps</button></h4>
						<div className="container">
							<br></br>
							<br></br>
							<div className="results-container" id="major-results">
								<ul>{this.state.majors}</ul>
								{this.state.applications}
								{this.state.tuitions}
								{this.state.text}
							</div>
						</div>

					</div>
				</div>

				<div>
					<br></br>
				</div>

			</div>
		);
	}
}
