import React from 'react';
import PageNavbar from './PageNavbar';
import CriteriaSearchRow from './CriteriaSearchRow';
import '../style/CriteriaSearch.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class CriteriaSearch extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			selectedState: "",
			selectedControl: "",
			selectedRegion: "",
			selectedLevel: "",
			selectedSize: "",
			selectedTuitiontype: "",
			selectedHousing: "%",
			selectedMajor: "",
			selectedSelective: "101",
			states: [],
			controls: [],
			regions: [],
			levels: [],
			sizes: [],
			tuitiontypes: [],
			housings: [],
			majors: [],
			selectives: []
		};

		this.submitCriteria = this.submitCriteria.bind(this);
		this.handleChange = this.handleChange.bind(this);
	}

	componentDidMount() {
		fetch('http://localhost:8081/states', {
			method: 'GET'
		}).then(res => {
			return res.json();
		}).then(stateListObj => {

			let stateList = stateListObj.map((stateObj, i) =>
				<option key={i} value={stateObj.state}>
				{stateObj.state}
				</option>
			);

			this.setState({
				states: stateList,
				selectedState: "%",
			});

			//if(stateList.length > 0) {
			//	this.setState({
			//		//selectedState: stateListObj[0].state,
			//		selectedState: "%",
			//	})
			//}
		});

		fetch('http://localhost:8081/controls', {
			method: 'GET'
		}).then(res => {
			return res.json();
		}).then(controlListObj => {

			let controlList = controlListObj.map((controlObj, i) =>
				<option key={i} value={controlObj.control}>
				{controlObj.control}
				</option>
			);

			this.setState({
				controls: controlList,
				selectedControl: "%",
			});

			//if(controlList.length > 0) {
			//	this.setState({
			//		//selectedControl: controlListObj[0].control,
			//		selectedControl: "%",
			//	})
			//}
		});

		fetch('http://localhost:8081/regions', {
			method: 'GET'
		}).then(res => {
			return res.json();
		}).then(regionListObj => {

			let regionList = regionListObj.map((regionObj, i) =>
				<option key={i} value={regionObj.region}>
				{regionObj.region}
				</option>
			);

			this.setState({
				regions: regionList,
				selectedRegion: "%",
			});

			//if(regionList.length > 0) {
			//	this.setState({
			//		//selectedRegion: regionListObj[0].region,
			//		selectedRegion: "%",
			//	})
			//}
		});

		fetch('http://localhost:8081/levels', {
			method: 'GET'
		}).then(res => {
			return res.json();
		}).then(levelListObj => {

			let levelList = levelListObj.map((levelObj, i) =>
				<option key={i} value={levelObj.level}>
				{levelObj.level}
				</option>
			);

			this.setState({
				levels: levelList,
				selectedLevel: "%",
			});

			//if(levelList.length > 0) {
			//	this.setState({
			//		//selectedLevel: levelListObj[0].level,
			//		selectedLevel: "%",
			//	})
			//}
		});

		fetch('http://localhost:8081/sizes', {
			method: 'GET'
		}).then(res => {
			return res.json();
		}).then(sizeListObj => {

			let sizeList = sizeListObj.map((sizeObj, i) =>
				<option key={i} value={sizeObj.size}>
				{sizeObj.size}
				</option>
			);

			this.setState({
				sizes: sizeList,
				selectedSize: "%",
			});

			//if(sizeList.length > 0) {
			//	this.setState({
			//		//selectedSize: sizeListObj[0].size,
			//		selectedSize: "%",
			//	})
			//}
		});

		fetch('http://localhost:8081/tuitiontypes', {
			method: 'GET'
		}).then(res => {
			return res.json();
		}).then(tuitiontypeListObj => {

			let tuitiontypeList = tuitiontypeListObj.map((tuitiontypeObj, i) =>
				<option key={i} value={tuitiontypeObj.tuitiontype}>
				{tuitiontypeObj.tuitiontype}
				</option>
			);

