import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import Media from 'react-bootstrap/Media'
import Button from 'react-bootstrap/Button'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

export default class CollegeListRow extends React.Component {
	constructor(props) {
		super(props);
	}

	checkLogoURL(apiurl) {
			// Send an HTTP request to the server to get regions drop down values
		fetch(apiurl)
			.then((response) => {
				if (response.status === 200) {
					//console.log("SUCCESSS" + apiurl);
					return "SUCCESSS";
				} else if (response.status === 408) {
					//console.log("SOMETHING WENT WRONG");
					return "university.png";
				}
			}, err => {
				// Print the error if there is one.
				console.log(err);
			});
	}

/* Contents of the HTML elements to show a College row. */
render() {
	var websiteURL = (this.props.url_school === "N/A") ? `https://www.google.com/search?q=${this.props.name}/` : "//" + this.props.url_school;
	var logoURL = "//logo.clearbit.com/" + this.props.url_school;
	//var URLresponse = this.checkLogoURL("//logo.clearbit.com/" + this.props.url_school);
	//console.log("URLresponse" + URLresponse);
	var viewbookURL = "http://localhost:8081/viewbook/" + this.props.id;

	return (

		<Media>
			<img
				width={100}
				height={100}
				className="align-self-start mr-3"
				src={logoURL}
				alt={this.props.name}
			/>
			<Media.Body>
				<h4>{this.props.name}</h4>
				<Row>
					<Col>{this.props.location}</Col>
					<Col><Button href={viewbookURL} variant="primary">View School</Button></Col>
				</Row>
				<Row><Col><a href={websiteURL} target="_blank" rel="noreferrer noopener">{this.props.url_school}</a></Col></Row>

				<Row><Col>{this.props.attribute1Label} : <span className="number-callout-small">{this.props.attribute1Value}</span></Col></Row>
				<p><i>Based on data available for year {this.props.data_year}.</i></p>
				<hr className="narrow"></hr>
			</Media.Body>
		</Media>

	);
}
}
