import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import '../style/CollegeViewbook.css';

export default class CollegeViewbook extends React.Component {
	constructor(props) {
        super(props);

		/* Make any necessary changes to state */
		this.state = {
			majors: []
		}
	}

	/* ---- Add methods and modify render function below ---- */
	componentDidMount() {
		console.log("what");
		fetch("http://localhost:8081/viewbook-majors", {
			method: 'GET',
		}).then(res => {
			return res.json();
		}, err => {
			console.log(err);
		}
		).then(majorsList => {
			if(!majorsList) return;
			let majorsDivs = majorsList.map((majorObj, i) =>
			<option key={i} value={majorObj.major}>{majorObj.major}</option>
			);

			//set state
			this.setState({
				majors: majorsDivs
			})
		}, err => {
			console.log(err);
		});
	}


	/* For sake of testing, html code from movie method here */

	render() {
		return (
			<div className="CollegeViewbook">

				<br></br>
				<div className="container viewbook-container"> 
					<div className="jumbotron">
						<div className="title"><h5>title or whatever</h5></div>
					</div>
				</div> 

				<br></br>
				<div><h4>Images go here</h4></div>

				<div><h4>Quick Facts</h4></div>

				<div>
					<div>
						<h4>Available Majors</h4>
							<div>{this.state.majors}</div>
					</div>
				</div>

			</div>
		);
	}
}
