import React from 'react';
import PageNavbar from './PageNavbar';
import '../style/CompareEarningsJob.css';
import 'bootstrap/dist/css/bootstrap.min.css'

export default class CompareEarningsJob extends React.Component {
	constructor(props) {
        super(props);

        /* Make any necessary changes to state */
		this.state = {
			selectedMajorID: "",
			majorDivObjs: [],
			selectedDegreeLevel: "%",
			degreeLevels: []
		};

		this.submitMajors = this.submitMajors.bind(this);
		this.handleChange = this.handleChange.bind(this);
		this.handleChangeDegree = this.handleChangeDegree.bind(this);
	}

	/* ---- Q3a (Best Genres) ---- */
	componentDidMount() {
		// Send an HTTP request to the server.
		var majorMap = {"Agriculture Related Sciences":1, "Natural Resources and Conservation":3, "Architecture":4, 
		"Communication/Journalism":4, "Computer Information Sciences":11, "Education":13, "Engineering":14, "Engineering Technology/Technician":15,
		"Foreign Language/Literature": 16, "Consumer/Human Sciences": 19, "Law": 22, "English Literature": 23, "Liberal Arts/Humanities": 24, 
		"Biology/Biomedical Science": 26, "Mathematics/Statistics":27, "Parks and Recreation":31, "Philosophy/Religion":38, "Theology":39, "Physical Sciences":40,
		"Psychology":42, "Social Sciences":45, "Mechanic and Repair Technician":47, "Visual and Performing Arts":50, "Health Professional":51, "Business Administration":52, "History":54};

		var majorOptions = [];

		Object.keys(majorMap).forEach(function(key) {
			majorOptions.push(<option value={majorMap[key]}>{key}</option>)
		});

		this.setState({
			majors: majorOptions
		});

		fetch('http://localhost:8081/degree-levels', {
			method: 'GET'
		}).then(res => {
			return res.json();
		}).then(degreeListObj => {
			let degreeList = degreeListObj.map((degreeObj, i) =>
				<option key={i} value={degreeObj.degree_level}>
					{degreeObj.degree_level}
				</option>
			);

			this.setState({
				degreeLevels: degreeList,
			});
		});
	}

	handleChange(e) {
		this.setState({
			selectedMajorID: e.target.value	
		});
	}

	handleChangeDegree(e) {
		this.setState({
			selectedDegreeLevel: e.target.value	
		});
	}

	/* ---- Q3b (Best Genres) ---- */
	submitMajors() {
		console.log(this.state.selectedDegreeLevel);
		fetch("http://localhost:8081/Compare-Earnings/" + this.state.selectedMajorID + "/" + this.state.selectedDegreeLevel,
		{
			method: "GET"
		}).then(res => {
			return res.json();
		}, err => {
			console.log(err);
		}).then(majorList => {
			let majorDivs = majorList.map((majorObj, i) => 
				<li key={i} className="table-row">
					<div className="col col-1">{majorObj.major}</div>
					<div className="col col-2">{majorObj.median_starting_sal}</div>
					<div className="col col-3">{majorObj.median_salary}</div>
					<div className="col col-4">{majorObj.median_debt}</div>
					<div className="col col-5">{majorObj.ROI}</div>
					<div className="col col-6">{majorObj.job_openings_10_yr_change}</div>
					<div className="col col-7">{majorObj.degree_level}</div>
					<div className="col col-8">{majorObj.typical_work_experience}</div>
				</li>
			);

			//This saves our HTML representation of the data into the state, which we can call in our render function
			this.setState({
				majorDivObjs: majorDivs
			});
		});
	}

	render() {

		return (
			<div className="CompareEarningsJob">
				<PageNavbar active="compareearnings" />

				<div className="container compareearnings-container">
					<div className="jumbotron">
						<div className="h5">Major Category</div>

						<div className="majors-container">
							<div className="dropdown-container">
								<select value={this.state.selectedMajorID} onChange={this.handleChange} className="dropdown" id="majorsDropdown">
									<option select value> -- major -- </option>
									{this.state.majors}
								</select>
							</div>
						</div>
						<div className="h5">Degree Level</div>
						<div className="degrees-container">
							<div className="dropdown-container">
								<select value={this.state.selectedDegreeLevel} onChange={this.handleChangeDegree} className="dropdown" id="degreesDropdown">
									<option value="%"> -- degree -- </option>
									{this.state.degreeLevels}
								</select>
								<button className="submit-btn" id="majorsSubmitBtn" onClick={this.submitMajors}>Submit</button>
							</div>
						</div>
					</div>
					<div className="jumbotron">
						<div className="college-container">
							<ul class="table">
								<li class="table-header">
									<div className="col col-1"><strong><p>Major</p></strong></div>
									<div className="col col-2"><strong><p>Median Starting Salary</p></strong></div>
									<div className="col col-3"><strong><p>Median Overall Salary</p></strong></div>
									<div className="col col-4"><strong><p>Median Debt</p></strong></div>
									<div className="col col-5"><strong><p>ROI</p></strong></div>
									<div className="col col-6"><strong><p>Job Openings Growth</p></strong></div>
									<div className="col col-7"><strong><p>Degree Level</p></strong></div>
									<div className="col col-8"><strong><p>Typical Work Experience</p></strong></div>
								</li>
								{this.state.majorDivObjs}
							</ul>
						</div>
					</div>
				</div>
			</div>
		);
	}
}
