import React from 'react';
import PageNavbar from './PageNavbar';
import CompareEarningsRow from './CompareEarningsRow';
import '../style/CompareEarningsJob.css';
import 'bootstrap/dist/css/bootstrap.min.css'

export default class CompareEarningsJob extends React.Component {
	constructor(props) {
        super(props);

        /* Make any necessary changes to state */
		this.state = {
			majorMap: {"Agriculture Related Sciences":1, "Natural Resources & Conservation":3, "Architecture":4, 
			"Communication/Journalism":4, "Computer Information Sciences":11, "Education":13, "Engineering":14},
			majorID: 0,
			majorDivObjs: []
		};

		this.submitMajors = this.submitMajors.bind(this);
		this.handleChange = this.handleChange.bind(this);
	}

	/* ---- Q3a (Best Genres) ---- */
	componentDidMount() {
		// Send an HTTP request to the server.
		var majorOptions = [];
		// 	<option value={this.state.majorMap["Agriculture Related Sciences"]}>Agriculture Related Sciences</option>,
		// 	<option value={this.state.majorMap["Natural Resources & Conservation"]}>Natural Resources andConservation</option>
		// ];

		Object.keys(this.state.majorMap).forEach(key => 
			majorOptions.push(<option value={this.state.majorMap[key]}>{key}</option>));

		this.setState({
			majors: majorOptions
		});
	}

	handleChange(e) {
		this.setState({
			majorRange: e.target.value
		});
		console.log(this.majorRange)
	}

	/* ---- Q3b (Best Genres) ---- */
	submitMajors() {
		fetch("http://localhost:8081/Compare-Earnings/" + this.state.selectedMajors,
		{
			method: "GET"
		}).then(res => {
			return res.json();
		}, err => {
			console.log(err);
		}).then(majorList => {
			console.log(majorList); //displays your JSON object in the console
			let majorDivs = majorList.map((majorObj, i) => 

				<div key={i} className="collegeResults">
					<div className="major">{majorObj.major}</div>
					<div className="median_starting_sal">{majorObj.median_starting_sal}</div>
					<div className="median_salary">{majorObj.median_salary}</div>
					<div className="median_debt">{majorObj.median_debt}</div>
					<div className="roi">{majorObj.ROI}</div>
					<div className="job_openings_2019">J{majorObj.job_openings_2019}</div>
					<div className="job_openings_2029">{majorObj.job_openings_2029}</div>
					<div className="job_openings_10_yr_change">{majorObj.job_openings_10_yr_change}</div>
					<div className="degree_level">{majorObj.degree_level}</div>
					<div className="typical_education">{majorObj.typical_education}</div>
					<div className="typical_work_experience">{majorObj.typical_work_experience}</div>
				</div>
			);

			//This saves our HTML representation of the data into the state, which we can call in our render function
			this.setState({
				majorDivObjs: majorDivs
			});
		});
	}

	render() {

		return (
			<div className="CompareEarningsJob">
				<PageNavbar active="compareearnings" />

				<div className="container compareearnings-container">
					<div className="jumbotron">
						<div className="h5">Compare Earnings By Major</div>

						<div className="majors-container">
							<div className="dropdown-container">
								<select value={this.state.selectedMajors} onChange={this.handleChange} className="dropdown" id="majorsDropdown">
									<option select value> -- select an option -- </option>
									{this.state.majors}
								</select>
								<button className="submit-btn" id="majorsSubmitBtn" onClick={this.submitMajors}>Submit</button>
							</div>
						</div>
					</div>
					<div className="jumbotron">
						<div className="college-container">
							<div className="college">
								<div className="header"><strong>Major</strong></div>
								<div className="header"><strong>Median Starting</strong></div>
								<div className="header"><strong>Median Salary</strong></div>
								<div className="header"><strong>Median Debt</strong></div>
								<div className="header"><strong>Job Openings 2019</strong></div>
								<div className="header"><strong>Job Openings 2029</strong></div>
								<div className="header"><strong>Change in Job Openings</strong></div>
								<div className="header"><strong>Degree Level</strong></div>
								<div className="header"><strong>Typical Education</strong></div>
								<div className="header"><strong>Typical Work Experience</strong></div>
							</div>
							<div className="college-container" id="results">
								{this.state.majorDivObjs}
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}
