import React from 'react';
import '../style/Top10Lists.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import PageNavbar from './PageNavbar';
import CollegeListRow from './CollegeListRow';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Media from 'react-bootstrap/Media'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import Accordion from 'react-bootstrap/Accordion'
import Card from 'react-bootstrap/Card'
import Form from 'react-bootstrap/Form'
import Jumbotron from 'react-bootstrap/Jumbotron'

export default class Top10Lists extends React.Component {
	constructor(props) {
		super(props);

		// State maintained by this React component
		this.state = {
			//variables here 
			institution: 100654,
			majors: [],
			movieName: "",
			colleges: [],
			regions: [],
			urbanizations: [],
			selectedControl: "NULL",
			selectedSize: "NULL",
			selectedLevel: "NULL",
			selectedDegUrban: "NULL",
			selectedRegion: "NULL",
			selectedTutionType: "In state",
			selectedLevelOfStudy: "Undergraduate",
			sortOrder: "DESC"
		}

		this.showTop10ByAcceptanceRate = this.showTop10ByAcceptanceRate.bind(this);
		this.showTop10ByTutionCost = this.showTop10ByTutionCost.bind(this);
		this.showTop10ByEarnings = this.showTop10ByEarnings.bind(this);
		this.showTop10ByJobs = this.showTop10ByJobs.bind(this);
		this.handleRegionFilterChange = this.handleRegionFilterChange.bind(this);
		this.handleControlFilterChange = this.handleControlFilterChange.bind(this);
		this.handleSizeFilterChange = this.handleSizeFilterChange.bind(this);
		this.handleLevelFilterChange = this.handleLevelFilterChange.bind(this);
		this.handleDegUrbanFilterChange = this.handleDegUrbanFilterChange.bind(this);
	}

	// handle change of region dropdown filter
	handleRegionFilterChange(e) {
		this.setState({
			selectedRegion: e.target.value
		});
	}

	handleDegUrbanFilterChange(e) {
		this.setState({
			selectedDegUrban: e.target.value
		});
	}

	handleControlFilterChange(e) {
		this.setState({
			selectedControl: e.target.value
		});
		console.log("selectedControl: " + this.selectedControl);
	}

	handleSizeFilterChange(e) {
		this.setState({
			selectedSize: e.target.value
		});
	}

	handleLevelFilterChange(e) {
		this.setState({
			selectedLevel: e.target.value
		});
	}

	componentDidMount() {
		// Send an HTTP request to the server.
		fetch("http://localhost:8081/regions",
			{
				method: 'GET' // The type of HTTP request.
			}).then(res => {
				// Convert the response data to a JSON.
				return res.json();
			}, err => {
				// Print the error if there is one.
				console.log(err);
			}).then(regionList => {
				if (!regionList) return;
				console.log(regionList);
				// Map each decade in decadesList to an dropdown element:
				let regionDivs = regionList.map((regionObj, i) =>
					<option key={i} value={regionObj.region}> {regionObj.region} </option>
				);

				// Set the state of the decade list to the value returned by the HTTP response from the server.
				this.setState({
					regions: regionDivs
				});
			}, err => {
				// Print the error if there is one.
				console.log(err);
			});

		// Send an HTTP request to the server.
		fetch("http://localhost:8081/urbanization",
			{
				method: 'GET' // The type of HTTP request.
			}).then(res => {
				// Convert the response data to a JSON.
				return res.json();
			}, err => {
				// Print the error if there is one.
				console.log(err);
			}).then(urbanList => {
				if (!urbanList) return;
				console.log(urbanList);
				// Map each decade in decadesList to an dropdown element:
				let urbanDivs = urbanList.map((urbanObj, i) =>
					<option key={i} value={urbanObj.degree_urbanization}> {urbanObj.degree_urbanization} </option>
				);

				// Set the state of the decade list to the value returned by the HTTP response from the server.
				this.setState({
					urbanizations: urbanDivs
				});
			}, err => {
				// Print the error if there is one.
				console.log(err);
			});

	}

	showTop10ByTutionCost() {
		// Send an HTTP request to the server.
		fetch(`http://localhost:8081/top10/tution/${this.state.selectedControl}/${this.state.selectedSize}/${this.state.selectedLevel}/${this.state.selectedRegion}/${this.state.selectedDegUrban}/${this.state.sortOrder}`,
			{
				method: 'GET' // The type of HTTP request.
			}).then(res => {
				// Convert the response data to a JSON.
				return res.json();
			}, err => {
				// Print the error if there is one.
				console.log(err);
			}).then(collegeList => {
				if (!collegeList) return;
				// Map each attribute of a movie in this.state.movies to an HTML element
				let collegeDivs = collegeList.map((college, i) =>
					<CollegeListRow name={college.InstitutionName} id={college.InstitutionID} location={college.location} url_school={college.url_school}
								data_year={college.year} attribute1Label="Acceptance Rate" attribute1Value={college.acceptance_rate} />
				);

				// Set the state of the movies list to the value returned by the HTTP response from the server.
				this.setState({
					colleges: collegeDivs
				});
			}, err => {
				// Print the error if there is one.
				console.log(err);
			});
	}

	showTop10ByEarnings() {
		// Send an HTTP request to the server.
		fetch(`http://localhost:8081/top10/earning/${this.state.selectedControl}/${this.state.selectedSize}/${this.state.selectedLevel}/${this.state.selectedRegion}/${this.state.selectedDegUrban}/${this.state.sortOrder}`,
			{
				method: 'GET' // The type of HTTP request.
			}).then(res => {
				// Convert the response data to a JSON.
				return res.json();
			}, err => {
				// Print the error if there is one.
				console.log(err);
			}).then(collegeList => {
				if (!collegeList) return;
				// Map each attribute of a movie in this.state.movies to an HTML element
				let collegeDivs = collegeList.map((college, i) =>
					<CollegeListRow name={college.InstitutionName} id={college.InstitutionID} location={college.location} url_school={college.url_school}
								data_year={college.year} attribute1Label="Acceptance Rate" attribute1Value={college.acceptance_rate} />
				);

				// Set the state of the movies list to the value returned by the HTTP response from the server.
				this.setState({
					colleges: collegeDivs
				});
			}, err => {
				// Print the error if there is one.
				console.log(err);
			});
	}

	showTop10ByAcceptanceRate() {
		// Send an HTTP request to the server.
		fetch(`http://localhost:8081/top10/acceptance/${this.state.selectedControl}/${this.state.selectedSize}/${this.state.selectedLevel}/${this.state.selectedRegion}/${this.state.selectedDegUrban}/${this.state.sortOrder}`,
			{
				method: 'GET' // The type of HTTP request.
			}).then(res => {
				// Convert the response data to a JSON.
				return res.json();
			}, err => {
				// Print the error if there is one.
				console.log(err);
			}).then(collegeList => {
				if (!collegeList) return;
				// Map each attribute of a movie in this.state.movies to an HTML element
				let collegeDivs = collegeList.map((college, i) =>
					<CollegeListRow name={college.InstitutionName} id={college.InstitutionID} location={college.location} url_school={college.url_school}
								data_year={college.year} attribute1Label="Acceptance Rate" attribute1Value={college.acceptance_rate} />
				);

				// Set the state of the movies list to the value returned by the HTTP response from the server.
				this.setState({
					colleges: collegeDivs
				});
			}, err => {
				// Print the error if there is one.
				console.log(err);
			});
	}

	showTop10ByJobs() {
		console.log("Top 10 Jobs");
	}

	//Need a render function. I'm copying sample method from HW2 if you need inspiration/structure
	// // {this.state.colleges}
	render() {

		return (

			<div className="Top10Lists">

				<Container fluid>
					<Row><br></br></Row>

					<Row class="d-flex flex-wrap align-items-center">
						<Col> </Col>
						<Col xs={8}><Media>
							<img
								width={119}
								height={100}
								className="mr-3"
								src="LogosTop10.jpg"
								alt="Top 10 Colleges"
							/>
							<Media.Body>
								<h2>TOP 10 Colleges</h2>
								<p>
									Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Sed arcu non odio euismod lacinia at. Mauris sit amet massa vitae tortor condimentum. Nisi vitae suscipit tellus mauris a diam maecenas sed enim. Sed augue lacus viverra vitae congue eu consequat ac felis.
    							</p>
							</Media.Body>
						</Media></Col>
						<Col> </Col>
					</Row>

					<Row><br></br></Row>

					<Row>

						<Col > </Col>
						<Col xs={2}> <Button variant="outline-primary" size="lg" >
							<img class="option-btn-img" src="./accepted.png" alt="Top 10 Colleges by Acceptance Rate" onClick={this.showTop10ByAcceptanceRate.bind()} />
								Top 10 Colleges by Acceptance Rate
								</Button>{' '}</Col>
						<Col xs={2}> <Button variant="outline-primary" size="lg" >
							<img class="option-btn-img" src="./cost.png" alt="Top 10 Colleges by Tution Fee" onClick={this.showTop10ByTutionCost.bind()} />
							Tution Fee
								</Button>{' '}</Col>
						<Col xs={2}> <Button variant="outline-primary" size="lg" >
							<img class="option-btn-img" src="./salary.png" alt="Top 10 Colleges by Earnings" onClick={this.showTop10ByEarnings.bind()} />
							Earnings
								</Button>{' '}</Col>
						<Col xs={2}> <Button variant="outline-primary" size="lg" >
							<img class="option-btn-img" src="./jobs.png" alt="Top 10 Colleges by Job Specialization" onClick={this.showTop10ByJobs.bind()} />
							Top 10 Colleges by Job Specialization
								</Button>{' '}</Col>
						<Col > </Col>
					</Row>

					<Row><br></br></Row>

					<Row>
						<Col> </Col>
						<Col xs={8}>
							<Accordion >
								<Card>
									<Accordion.Toggle as={Card.Header} eventKey="0">
										FILTER YOUR RESULTS
    								</Accordion.Toggle>
									<Accordion.Collapse eventKey="0">
										<Card.Body>
											<Form noValidate>
												<Form.Row>
													<Form.Group as={Col} md="4" controlId="filterControl">
														<Form.Label>Control of Institution</Form.Label>
														<Form.Control as="select" multiple name="controlFilter" value={this.state.selectedControl} onChange={this.handleControlFilterChange}>
															<option value="Public"> Public </option>
															<option value="Private not-for-profit"> Private not-for-profit </option>
															<option value="Private for-profit"> Private for-profit </option>
														</Form.Control>
													</Form.Group>
													<Form.Group as={Col} md="4" controlId="filterSize">
														<Form.Label>Institution Size</Form.Label>
														<Form.Control as="select" multiple name="sizeFilter" value={this.state.selectedSize} onChange={this.handleSizeFilterChange}>
															<option value="Under 1,000"> Under 1,000 </option>
															<option value="1,0004,999"> 1,0004,999 </option>
															<option value="5,0009,999"> 5,0009,999 </option>
															<option value="10,00019,999"> 10,00019,999 </option>
															<option value="20,000 and above"> 20,000 and above </option>
														</Form.Control>
													</Form.Group>
													<Form.Group as={Col} md="4" controlId="filterLevel">
														<Form.Label>Institution Level</Form.Label>
														<Form.Control as="select" multiple name="controlSize" value={this.state.selectedLevel} onChange={this.handleLevelFilterChange}>
															<option value="Less than two years (below associate's)"> Less than two years (below associate's) </option>
															<option value="At least two but less than four years"> At least two but less than four years </option>
															<option value="Four or more years"> Four or more years </option>
														</Form.Control>
													</Form.Group>
												</Form.Row>
												<Form.Row>
													<Form.Group as={Col} md="6" controlId="filterRegion">
														<Form.Label>Region</Form.Label>
														<Form.Control as="select" multiple name="regionFilter" value={this.state.selectedRegion} onChange={this.handleRegionFilterChange}>
															{this.state.regions}
														</Form.Control>
													</Form.Group>
													<Form.Group as={Col} md="6" controlId="filterDegUrban">
														<Form.Label>Degree of Urbanization</Form.Label>
														<Form.Control as="select" multiple name="degUrbanFilter" value={this.state.selectedDegUrban} onChange={this.handleDegUrbanFilterChange}>
															{this.state.urbanizations}
														</Form.Control>
													</Form.Group>
												</Form.Row>
												<Form.Row>
													<Form.Group as={Col} md="2" controlId="filterDegUrban">
														<Button type="submit">Filter Results</Button>
													</Form.Group>
													<Form.Group as={Col} md="1" controlId="filterDegUrban">
														<Button type="reset">Clear</Button>
													</Form.Group>
												</Form.Row>
											</Form>
										</Card.Body>
									</Accordion.Collapse>
								</Card>
								<Card> </Card>
							</Accordion>

						</Col>
						<Col> </Col>
					</Row>

					<Row><br></br></Row>
					<Row>
						<Col> </Col>
						<Col xs={7}>{this.state.colleges}</Col>
						<Col> </Col>
					</Row>

				</Container>

			</div>
		);
	}
}
